document.addEventListener("DOMContentLoaded",()=>{
  const nav=document.querySelector("#nav-toggle");
  nav&&nav.addEventListener("click",()=>document.documentElement.classList.toggle("menu-open"));

  const btn=document.querySelector("#mode"), root=document.documentElement;
  if(btn){
    root.dataset.theme=localStorage.theme||"light";
    btn.addEventListener("click",()=>{
      root.dataset.theme=root.dataset.theme==="dark"?"light":"dark";
      localStorage.theme=root.dataset.theme;
    });
  }
});
