/* tiny, plain-JS dark-mode helper  –  530 bytes un-minified               */
/* ---------------------------------------------------------------------- */
document.addEventListener('DOMContentLoaded', () => {
  const html   = document.documentElement;   // <html>
  const key    = 'theme';                    // localStorage key
  const toggle = document.getElementById('mode');

  /* 1.  apply stored choice  (or fall back to DARK) ------------------- */
  const pref  = localStorage.getItem(key);
  html.dataset.theme = (pref === 'light') ? 'light' : 'dark';

  /* 2.  click  →  switch attribute  +  remember ---------------------- */
  toggle.addEventListener('click', () => {
    const nowDark = html.dataset.theme === 'dark';
    html.dataset.theme = nowDark ? 'light' : 'dark';
    localStorage.setItem(key, nowDark ? 'light' : 'dark');
  });
});
