document.addEventListener("DOMContentLoaded",function(){(()=>{const d=document.documentElement,k="theme",btn=document.getElementById("mode");const pref=localStorage.getItem(k);pref==="light"?d.removeAttribute("data-theme"):d.dataset.theme="dark";btn&&btn.addEventListener("click",()=>{const dark=d.dataset.theme==="dark";dark?(d.removeAttribute("data-theme"),localStorage.setItem(k,"light")):(d.dataset.theme="dark",localStorage.setItem(k,"dark"))});})();});
