(()=>{const d=document.documentElement,k="theme",btn=document.getElementById("mode"); /* Apply stored choice on load */ 
if(localStorage.getItem(k)==="dark"){d.dataset.theme="dark";}
/* Toggle & remember */
btn&&btn.addEventListener("click",()=>{if(d.dataset.theme==="dark"){d.removeAttribute("data-theme"),localStorage.removeItem(k);}else{d.dataset.theme="dark",localStorage.setItem(k,"dark");}});})();
